mapper.select:
SELECT LINK_ID,LINK_NAME,ENTRACE FROM t_business_link_manage_table WHERE LINK_ID in
#{item}
and IS_DELETED = 0 AND persistence = 1

select
manage.LINK_ID ,
manage.LINK_NAME,
manage.ENTRACE,
manage.USER_ID,
manage.CREATE_TIME,
manage.UPDATE_TIME,
manage.IS_CHANGE as BUSINESS_IS_CHANGE,
manage.CAN_DELETE as BUSINESS_CAN_DELETE,
manage.IS_CORE as BUSINESS_IS_CORE,
manage.BUSINESS_DOMAIN,
manage.LINK_LEVEL,
middleware.ID,
tech.LINK_ID as TECH_LINK_ID,
tech.LINK_NAME as TECH_LINK_NAME

from t_business_link_manage_table manage left join t_middleware_link_relate relate
on manage.RELATED_TECH_LINK = relate.TECH_LINK_ID
left join t_middleware_info middleware
on middleware.ID = relate.MIDDLEWARE_ID left join t_link_manage_table tech
on manage.RELATED_TECH_LINK = tech.LINK_ID


        and manage.LINK_NAME = #{query.name}


        and manage.ENTRACE = #{query.entrance,jdbcType=VARCHAR}


        and manage.IS_CHANGE = #{query.isChange,jdbcType=TINYINT}


        and tech.LINK_NAME = #{query.systemProcessName}


        and middleware.MIDDLEWARE_TYPE = #{query.middleWareType}


        and middleware.MIDDLEWARE_NAME = #{query.middleWareName}


        and middleware.MIDDLEWARE_VERSION = #{query.middleWareVersion}


        and manage.LINK_ID = #{query.id}


        and manage.BUSINESS_DOMAIN = #{query.domain}


        AND manage.user_id IN

            #{userId}


    and manage.IS_DELETED = 0

group by manage.LINK_ID order by manage.CREATE_TIME desc

mapper.update:
update t_business_link_manage_table
set ENTRACE = #{newEntrance}
where RELATED_TECH_LINK = #{linkId}
  and IS_DELETED = 0

mapper.select:
