mapper.update:
UPDATE t_application_mnt
SET node_num = #{param.nodeNum,jdbcType=INTEGER}
WHERE application_name =
      #{param.appName,jdbcType=VARCHAR}
  AND env_code = #{envCode}
  AND tenant_id = #{tenantId,jdbcType=BIGINT}

