SELECT t1.*, t2.* FROM t_application_mnt t1 LEFT JOIN t_white_list t2 ON t1.APPLICATION_ID = t2.APPLICATION_ID WHERE t1.APPLICATION_ID IN
#{applicationId}
ORDER BY t1.APPLICATION_ID

select application_id
from t_application_mnt
where application_name = #{applicationName}

SELECT a.APPLICATION_ID application_id, a.APPLICATION_NAME application_name,
       a.UPDATE_TIME update_time, a.ACCESS_STATUS access_status, u.`name` user_name, a.dept_id dept_id
FROM t_application_mnt a LEFT JOIN t_tro_user u ON u.id = a.USER_ID

WHERE a.tenant_id = #{param.tenantId} AND a.env_code = #{param.envCode}

    AND a.APPLICATION_NAME LIKE CONCAT('%', #{param.applicationName}, '%')



    AND a.ACCESS_STATUS = #{param.accessStatus}



    AND a.dept_id = #{deptId}



    AND a.USER_ID IN

        #{userId}




    AND a.dept_id IN

        #{deptId}




    and a.UPDATE_TIME   >=   #{param.updateStartTime}



    and a.UPDATE_TIME   <=  #{param.updateEndTime}


ORDER BY a.id DESC

SELECT a.APPLICATION_ID application_id, a.APPLICATION_NAME application_name,
a.UPDATE_TIME update_time, a.ACCESS_STATUS access_status, u.`name` user_name,
       a.NODE_NUM node_num , a.dept_id dept_id
FROM t_application_mnt a LEFT JOIN t_tro_user u ON u.id = a.USER_ID

WHERE a.tenant_id = #{param.tenantId} AND a.env_code = #{param.envCode}

    AND a.APPLICATION_NAME LIKE CONCAT('%', #{param.applicationName}, '%')


    AND a.APPLICATION_ID IN

        #{appId}




    AND a.USER_ID IN

        #{userId}



    AND a.dept_id IN

        #{deptId}


ORDER BY a.id DESC
